INSERT INTO `profil` (`id_profil`, `tahun_ajar`, `nama`, `tlp`, `alias`, `alamat`, `isi`, `akabest`) VALUES
(1, '2020/2021', 'STMIK PRINGSEWU ', '+62 7292-1124', 'SEKOLAH TINGGI MANJEMEN INFORMATIKA DAN KOMPUTER', 'Jalan wismarini No 09 Pringsewu Lampung', '<p>Program Studi :<br />\r\nProgram Studi Sistem Informasi (S1)<br />\r\nProgram Studi Manajemen Informatika (D3)<br />\r\nWaktu Pendaftaran:<br />\r\nPeriode 1 : 1 Januari &ndash; 5 Mei 2018<br />\r\nPeriode 1 : 6 Mei &ndash; 31 September 2018<br />\r\nTempat Pendaftaran:<br />\r\nJalan Wisma Rini No 09 Pringsewu Lampung (Kampus Pringsewu)<br />\r\nJalan Raya Wonokriyo Gading Rejo (Kampus Gading)<br />\r\nProgram KHUSUS KELAS XII&nbsp; *Bebas Biaya Sarana Prasarana 1 Semester (Rp. 500.000)*</p>\r\n\r\n<p><br />\r\nProsedur Pendaftaran:</p>\r\n\r\n<p>Calon Mahasiswa mendaftar secaran online melalui link pmb.stmikpringsewu.ac.id. Selanjutnya, Print out Formulir Perdaftaran online, kemudian diserahkan ke tempat pendaftaan.</p>\r\n', 'mardybest@gmail.com');
