INSERT INTO `tb_soal_essay` (`id_essay`, `id_tq`, `pertanyaan`, `gambar`, `tgl_buat`) VALUES
(2, 3, '<p style=\"text-align:justify\"><span style=\"font-size:12.0000pt\"><span style=\"font-family:\'Times New Roman\'\">Sebutkan pengguna internal dan eksternal akuntansi!</span></span></p>\r\n', '', '2020-09-06'),
(3, 3, '<p><span style=\"font-size:12.0000pt\"><span style=\"font-family:\'Times New Roman\'\">Apakah yang dimaksud dengan aktiva? Sebutkan unsur-unsurnya!</span></span></p>\r\n', '', '2020-09-06'),
(4, 3, '<p><span style=\"font-size:12.0000pt\"><span style=\"font-family:\'Times New Roman\'\">Apakah yang dimaksud dengan pasiva? Sebutkan unsur-unsurnya!</span></span></p>\r\n', '', '2020-09-06'),
(5, 3, '<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12.0000pt\"><span style=\"font-family:\'Times New Roman\'\">Mengapa perkiraan kewajiban harus dicatat lebih dulu daripada perkiraan modal?</span></span></li>\r\n</ol>\r\n', '', '2020-09-06'),
(6, 3, '<p><span style=\"font-size:12.0000pt\"><span style=\"font-family:\'Times New Roman\'\">Sebutkan kelompok perkiraan utama secara berurutan (dari nomor 1-5)!</span></span></p>\r\n', '', '2020-09-06'),
(7, 3, '<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12.0000pt\"><span style=\"font-family:\'Times New Roman\'\">Jelaskan secara singkat 5 fungsi jurnal!</span></span></li>\r\n</ol>\r\n', '', '2020-09-06'),
(8, 3, '<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12.0000pt\"><span style=\"font-family:\'Times New Roman\'\">Jelaskan macam-macam sistem pengkodean perkiraan!</span></span></li>\r\n</ol>\r\n', '', '2020-09-06'),
(9, 3, '<ol>\r\n	<li><span style=\"font-size:12.0000pt\"><span style=\"font-family:\'Times New Roman\'\">Sebutkan dan jelaskan fungsi Buku Besar!</span></span></li>\r\n</ol>\r\n', '', '2020-09-06'),
(10, 3, '<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12.0000pt\"><span style=\"font-family:\'Times New Roman\'\">Jelaskan fungsi kolom &ldquo;kode perkiraan&rdquo; dalam neraca saldo!</span></span></li>\r\n</ol>\r\n', '', '2020-09-06'),
(11, 3, '<ol>\r\n	<li><span style=\"font-size:12.0000pt\"><span style=\"font-family:\'Times New Roman\'\">Apa fungsi kolom &ldquo;ref&rdquo; dalam Jurnal Umum dan Buku Besar?</span></span></li>\r\n</ol>\r\n', '', '2020-09-06'),
(12, 2, '<p><span style=\"font-size:11.0000pt\"><span style=\"font-family:Calibri\">apa saja yang dimaksud dengan unsure pokok ajaran agama islam sebut kan dan jelaskan?</span></span></p>\r\n', '', '2020-09-06'),
(13, 2, '<p><span style=\"font-size:11.0000pt\"><span style=\"font-family:Calibri\">apa saja keterkaitan antara aqida syariah dan ahlak jelaskan ? dan ketiganya diibaratkan sebagai apa,,jelaskan?</span></span></p>\r\n', '', '2020-09-06'),
