(89, 12, '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ada beberapa aspek faktor manusia yang melakukan interaksi dengan komputer kecuali....</span></span></p>\r\n\r\n<p style=\"margin-left:21.3pt\">&nbsp;</p>\r\n', '', '<p style=\"margin-left:21.3pt\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Pengalihan</span></span></p>\r\n\r\n<p style=\"margin-left:20.25pt\">&nbsp;</p>\r\n', '<p style=\"margin-left:20.25pt\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Pendengaran</span></span></p>\r\n', '<p style=\"margin-left:20.25pt\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Sentuhan </span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Kecepatan</span></span></p>\r\n', '', 'D', '2020-09-08'),
