(91, 12, '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Sebutkan faktor yang menjadi pusat evaluasi dalam faktor manusia terukur...</span></span></p>\r\n', '', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Penjadualan dan anggaran</span></span></p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Standarisasi</span></span></p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Waktu belajar</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Desain</span></span></p>\r\n', '', 'C', '2020-09-08'),
(92, 12, '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tujuan perencanaan Antarmuka Pemakai yaitu...</span></span></p>\r\n', '', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Penentuan sarana masysrakat pemakai dan tugas-tugasnya sangat penting</span></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;</p>\r\n', '<p style=\"margin-left:42.55pt\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tujuan tingkat tinggi yaitu membuat kualitas hidup pemakai lebih baik memang penting untuk diingat</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Fungsionalitas harus lengkap</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Minat yang besar terhadap faktor manusia pada sistm interaktif</span></span></p>\r\n', '', 'A', '2020-09-08'),
(93, 12, '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Banyaknya cahaya yang dipantulkan permukaan objek adalah pengertian dari.....</span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n</ol>\r\n', '', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Kontras</span></span></p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Luminans</span></span></p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Kecerahan</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Daerah penglihatan</span></span></p>\r\n', '', 'B', '2020-09-08'),
(94, 12, '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Penggunaan warna yang bertujuan untuk menarik perhatian atau mengelompokan informasi. Sehingga tidak perlu menggambarkan warna yang berlebihan. Adalah salah satu pengertian dari aspek.....</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Aspek persepsi</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Aspek psikologi</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Aspek kognitif</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Aspek multimedia</span></span></p>\r\n', '', 'C', '2020-09-08'),
(95, 12, '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Memori yang bekerja sebagai tempat penyimpanan sementara untuk menerima ransangan dari indera adalah pengertian dari.....</span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:red\">Memori sensor</span></span></span></strong></li>\r\n	<li><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Memori jangka panjang</span></span></li>\r\n	<li><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Memori jangka pendek</span></span></li>\r\n	<li><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Memori jangka sedang</span></span></li>\r\n</ol>\r\n', '', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Memori sensor</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Memori jangka panjang</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Memori jangka pendek</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Memori jangka sedang</span></span></p>\r\n', '', 'A', '2020-09-08'),
(96, 12, '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Daerah-daerah apa saja yang menjadi daerah penglihatan.....</span></span></p>\r\n', '', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Sensor</span></span></p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Feedback</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Hue</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Binokuler</span></span></p>\r\n', '', 'D', '2020-09-08'),
