(98, 12, '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Faktor manusia dapat dipandang sebagai sistem pemroses informasi adalah. <em>Kecuali</em>.....</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Informasi diterima melalui saluran folder/data</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Informasi diterima dan ditanggapi melalui saluran input-output (indera)</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Informasi disimpan dalam ingatan (memori)</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Informasi di proses dan diaplikasikan dalam berbagai cara</span></span></p>\r\n', '', 'A', '2020-09-08'),
